Extensions.ExtractScript:
param([xml] $Document);
foreach( $file in $Document.unattend.Extensions.File ) {
    $path = [System.Environment]::ExpandEnvironmentVariables( $file.GetAttribute( 'path' ) );
    mkdir -Path( $path | Split-Path -Parent ) -ErrorAction 'SilentlyContinue';
    $encoding = switch( [System.IO.Path]::GetExtension( $path ) ) {
        { $_ -in '.ps1', '.xml' } { [System.Text.Encoding]::UTF8; }
        { $_ -in '.reg', '.vbs', '.js' } { [System.Text.UnicodeEncoding]::new( $false, $true ); }
        default { [System.Text.Encoding]::Default; }
    };
    $bytes = $encoding.GetPreamble() + $encoding.GetBytes( $file.InnerText.Trim() );
    [System.IO.File]::WriteAllBytes( $path, $bytes );
}

Extensions.File:
$selectors = @(
	'Microsoft.549981C3F5F10';
	'Microsoft.BingNews';
	'Clipchamp.Clipchamp';
	'MicrosoftTeams';
);
$getCommand = { Get-AppxProvisionedPackage -Online; };
$filterCommand = { $_.DisplayName -eq $selector; };
$removeCommand = {
  [CmdletBinding()]
  param([Parameter( Mandatory, ValueFromPipeline )] $InputObject);
  process { $InputObject | Remove-AppxProvisionedPackage -AllUsers -Online -ErrorAction 'Continue'; }
};
$type = 'Package';
$logfile = 'C:\Windows\Setup\Scripts\RemovePackages.log';
& {
	$installed = & $getCommand;
	foreach( $selector in $selectors ) {
		$result = [ordered] @{ Selector = $selector; };
		$found = $installed | Where-Object -FilterScript $filterCommand;
		if( $found ) {
			$result.Output = $found | & $removeCommand;
			if( $? ) { $result.Message = "$type removed."; }
			else { $result.Message = "$type not removed."; $result.Error = $Error[0]; }
		} else { $result.Message = "$type not installed."; }
		$result | ConvertTo-Json -Depth 3 -Compress;
	}
} *>&1 >> $logfile;

$scripts = @(
	{ Remove-Item -LiteralPath 'C:\Users\Default\AppData\Roaming\Microsoft\Windows\Start Menu\Programs\OneDrive.lnk' -ErrorAction 'Continue'; };
	{ reg.exe add "HKLM\SOFTWARE\Microsoft\Windows\CurrentVersion\Communications" /v ConfigureChatAutoInstall /t REG_DWORD /d 0 /f; };
	{ reg.exe add "HKLM\SOFTWARE\Policies\Microsoft\Windows\WindowsCopilot" /v TurnOffWindowsCopilot /t REG_DWORD /d 1 /f; };
	{ Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\RemovePackages.ps1' -Raw | Invoke-Expression; };
	{ net.exe accounts /maxpwage:UNLIMITED; };
);
& {
  foreach( $script in $scripts ) { & $script; }
} *>&1 >> "C:\Windows\Setup\Scripts\Specialize.log";

$scripts = @(
	{ reg.exe add "HKU\DefaultUser\Software\Policies\Microsoft\Windows\WindowsCopilot" /v TurnOffWindowsCopilot /t REG_DWORD /d 1 /f; };
	{ reg.exe add "HKU\DefaultUser\Software\Microsoft\Windows\CurrentVersion\Explorer\Advanced" /v TaskbarAl /t REG_DWORD /d 0 /f; };
);
& {
  foreach( $script in $scripts ) { & $script; }
} *>&1 >> "C:\Windows\Setup\Scripts\DefaultUser.log";

$scripts = @(
	{ Set-ItemProperty -LiteralPath 'Registry::HKLM\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Winlogon' -Name 'AutoLogonCount' -Type 'DWord' -Force -Value 0; };
	{ reg.exe add "HKLM\SOFTWARE\Policies\Microsoft\Windows\System" /v NoLocalPasswordResetQuestions /t REG_DWORD /d 1 /f; };
	{ Remove-Item -LiteralPath 'C:\Windows\Panther\unattend.xml' -Force -ErrorAction 'SilentlyContinue'; };
);
& {
  foreach( $script in $scripts ) { & $script; }
} *>&1 >> "C:\Windows\Setup\Scripts\FirstLogon.log";
